package Tnsif_daythree;

public class Static {
	static int value=15;
	public void StaticDemo() {
		System.out.println("The number is : "+value);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Static s=new Static();
		s.StaticDemo();
		System.out.println("This is static work!");

	}

}
